mapper.update:
update contract
set status=#{status}
where id = #{id}

mapper.select:
select id, title, reason, type, status, create_time
from contract
where id = #{id}

select id, title, reason, type, status, create_time
from contract
where title like #{title}

select id, title, reason, type, status, create_time
from contract
where status = #{status}
